const userInput = ''; // Custom seed
const gender = Math.random() > 0.5 ? 'male' : 'female';
const seed = userInput || Math.floor(Math.random() * 100000);
const apis = [
  `https://avatars.dicebear.com/api/male/${seed}.svg`,
  `https://avatars.dicebear.com/api/female/${seed}.svg`,
  `https://api.multiavatar.com/${seed}.png`,
  `https://joeschmoe.io/api/v1/${gender}/${seed}`
];
const avatarUrls = apis.map(api => ({ url: api }));
return [{ json: { seed, gender, avatarUrls } }];

const { seed, gender, avatarUrls } = items[0].json;
let html = `<html><head><style>
  .gallery { display: flex; flex-wrap: wrap; }
  .avatar { margin: 10px; text-align: center; }
  img { width: 100px; height: 100px; }
</style></head><body>
<h1>Avatar Gallery</h1>
<p>Seed: ${seed}, Gender: ${gender}</p>
<div class='gallery'>`;
avatarUrls.forEach(({ url }) => {
  html += `<div class='avatar'>
    <img src='${url}' alt='Avatar'/>
    <a href='${url}' download>Download</a>
  </div>`;
});
html += '</div></body></html>';
return [{ json: { html } }];
